```javascript
// WebSocket 연결 및 실시간 업데이트
let ws;
let moistureChart;

function connectWebSocket() {
    ws = new WebSocket(`ws://${location.host}/ws`);

    ws.onopen = () => {
        document.getElementById('ws-status').className = 'status-dot connected';
        document.getElementById('ws-text').textContent = '실시간 연결됨';
    };

    ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'sensor_update') {
            updateZoneCards(data);
            updateChart(data);
        }
    };

    ws.onclose = () => {
        document.getElementById('ws-status').className = 'status-dot disconnected';
        document.getElementById('ws-text').textContent = '연결 끊김 - 재연결 중...';
        setTimeout(connectWebSocket, 3000); // 3초 후 재연결
    };
}

function updateZoneCards(data) {
    const container = document.getElementById('zones-container');
    // 구역 카드 업데이트 로직
    const moisture = data.moisture;
    const statusClass = moisture < 40 ? 'low' : moisture > 70 ? 'high' : 'ok';

    let card = document.getElementById(`zone-${data.zone}`);
    if (card) {
        card.querySelector('.moisture-value').textContent = moisture;
        card.querySelector('.moisture-value').className = `moisture-value ${statusClass}`;
    }
}

function toggleValve(channel) {
    const btn = document.getElementById(`valve-btn-${channel}`);
    const newState = btn.classList.contains('on') ? 0 : 1;
    ws.send(JSON.stringify({ action: 'valve', channel, state: newState }));
}

